'use strict';

var copyDeep     = require('es5-ext/object/copy-deep')
  , Password     = require('mano').db.Password
  , Email        = require('mano').db.Email;

exports.validate = function (data) {
	var normalizedData = copyDeep(data);
	normalizedData.email = Email.validate(data.email);
	normalizedData.password = Password.validate(data.password);
	return normalizedData;
};
