'use strict';

var setup = function () {
	var scripts, clear;
	if (!document.body) {
		setTimeout(setup, 100);
		return;
	}
	scripts = document.body.getElementsByTagName('script');
	clear = function () {
		var l = scripts.length - 1, i, script;
		for (i = l; (script = scripts[i]); --i) {
			if (script.parentNode && !script.src) script.parentNode.removeChild(script);
		}
	};
	clear();
	setInterval(clear, 300);
};
setup();
