'use strict';

var $ = require('./main');

require('./element#/event');
require('./element#/toggle');

module.exports = $.confirmSubmit = function (form, message) {
	if (!$(form)) return;
	if (message == null) message = "Are you sure?";
	$(form).addEvent('submit', function (e) {
		if (!confirm(message)) {
			$.stopPropagation(e);
			$.preventDefault(e);
		}
	}, true);
};
