'use strict';

var noop = function () {};

if ((typeof console === 'undefined') || !console) {
	exports.log = exports.warn = exports.error = noop;
} else {
	exports.log = (typeof console.log === 'function')
		? function (msg) {
			console.log(msg);
		} : noop;
	exports.warn = (typeof console.warn === 'function')
		? function (msg) {
			console.warn(msg);
		} : noop;
	exports.error = (typeof console.error === 'function')
		? function (msg) {
			console.error(msg);
		} : noop;
}
