'use strict';

module.exports = function (a, b) {
	if (a === b) return false;
	if (a.contains) {
		try {
			// IE throws exception when a is not within document
			return a.contains(b);
		} catch (e) {
			return false;
		}
	}
	if (a.compareDocumentPosition) {
		try {
			return !!(a.compareDocumentPosition(b) & 16);
		} catch (e) {
			return false;
		}
	}
	return null;
};
