'use strict';

var forIn = require('./for-in')

  , call = Function.prototype.call
  , ignored = { master: true, owner: true, key: true, forEach: true };

module.exports = $.dbjsMapForEach = function (obj, cb, thisArg) {
	forIn(obj, function (value, key) {
		if (ignored[key]) return;
		call.call(cb, thisArg, value, key, obj);
	});
};
