'use strict';

var main = module.exports = require('./main'), hasClass, addClass, removeClass;

main.hasClass = hasClass = function (className) {
	return (new RegExp("(^|\\s)" + className +
		"(\\s|$)")).test(this.className);
};

main.addClass = addClass = function (className) {
	if (!hasClass.call(this, className)) {
		this.className += (this.className ? " " : "") + className;
	}
	return this;
};

main.removeClass = removeClass = function (className) {
	this.className = this.className
		.replace(new RegExp("(^|\\s)" + className + "(\\s|$)", "g"), "$2");
	return this;
};

main.toggleClass = function (className) {
	if (hasClass.call(this, className)) removeClass.call(this, className);
	else addClass.call(this, className);
	return this;
};
