'use strict';

var $    = require('../main')
  , main = require('./class')

  , hasClass = main.hasClass;

module.exports = require('./main').getByClass = function (name, cls) {
	var result = [], els = this.getElementsByTagName(name), i, el;
	for (i = 0; (el = els[i]); ++i) {
		if (hasClass.call(el, cls)) result.push($(el));
	}
	return result;
};
