'use strict';

var $    = require('../main')
  , main = require('./main');

module.exports = main.parentByName = function (name) {
	var parent = this.parentNode;

	name = String(name).toLowerCase();

	while (parent && (parent.nodeType === 1)) {
		if (parent.nodeName.toLowerCase() === name) return $(parent);
		parent = parent.parentNode;
	}
	return null;
};
