'use strict';

var $ = require('./main')

  , hasOwnProperty = Object.prototype.hasOwnProperty
  , call = Function.prototype.call;

module.exports = $.forEach = function (obj, cb, thisArg) {
	var i, l;
	if (obj == null) throw new Error(obj + " is not an object");
	obj = Object(obj);
	l = obj.length >>> 0;
	for (i = 0; i < l; ++i) {
		if (hasOwnProperty.call(obj, i)) call.call(cb, thisArg, obj[i], i, obj);
	}
};
