'use strict';

var $ = require('./main')

  , call = Function.prototype.call;

module.exports = $.forIn = function (obj, cb, thisArg) {
	var i;
	if (obj == null) throw new Error(obj + " is not an object");
	obj = Object(obj);
	for (i in obj) {
		if (obj.propertyIsEnumerable(i)) call.call(cb, thisArg, obj[i], i, obj);
	}
};
