'use strict';

var getViewportRect = require('./get-viewport-rect');

module.exports = function (element) {
	var rect = {}, boundsRect, viewportRect;
	if (!element.getBoundingClientRect) return null;
	viewportRect = getViewportRect();
	boundsRect = element.getBoundingClientRect();
	rect.top = viewportRect.top + boundsRect.top;
	rect.left = viewportRect.left + boundsRect.left;
	rect.height = boundsRect.bottom - boundsRect.top;
	rect.width = boundsRect.right - boundsRect.left;
	return rect;
};
