'use strict';

module.exports = function (element) {
	var rect = {};
	rect.top = document.documentElement.scrollTop || document.body.scrollTop;
	rect.left = document.documentElement.scrollLeft || document.body.scrollLeft;
	if (window.innerWidth) {
		// Non IE
		rect.width = window.innerWidth;
		rect.height = window.innerHeight;
	} else if (document.documentElement &&
			(document.documentElement.clientWidth || document.documentElement.clientHeight)) {
		// IE 6+ in 'standards compliant mode'
		rect.width = document.documentElement.clientWidth;
		rect.height = document.documentElement.clientHeight;
	} else if (document.body && (document.body.clientWidth || document.body.clientHeight)) {
		//IE 4 compatible
		rect.width = document.body.clientWidth;
		rect.height = document.body.clientHeight;
	} else {
		return null;
	}
	return rect;
};
