// See:
// https://github.com/FortAwesome/Font-Awesome/issues/354
// http://stackoverflow.com/a/10557782/96806

'use strict';

if (!document.addEventListener) { // Address IE8 and lower
	require('./on-document-ready')(function () {
		var head = document.getElementsByTagName('head')[0]
		  , style = document.createElement('style');
		style.type = 'text/css';

		if (!style.styleSheet) return;

		style.styleSheet.cssText = ':before,:after{content:none !important';
		head.appendChild(style);
		setTimeout(function () { head.removeChild(style); }, 0);
	});
}
