'use strict';

var $ = require('./main')

  , indexOf = Array.prototype.indexOf;

module.exports = $.indexOf = function (arr, needle) {
	var length, i;
	if (indexOf) return indexOf.call(arr, needle);

	if (arr == null) throw new TypeError(arr + " is not a value");
	length = arr.length >>> 0;

	for (i = 0; i < length; i++) {
		if (!arr.hasOwnProperty(i)) continue;
		if (arr[i] === needle) return i;
	}
	return -1;
};
