'use strict';

var elProto = require('./element#/main')
  , console = require('./console')

  , data = {};

module.exports = function (el, nowarn) {
	var i, id = el;
	if (!el || (typeof el === 'string')) {
		el = document.getElementById(id);
		if (el) {
			data[id] = el;
		} else {
			if (data.hasOwnProperty(id)) return data[id];
			if (!nowarn) console.warn(id + " element not found");
			return null;
		}
	}
	if (el.__manoLegacyExtended) return el;
	for (i in elProto) {
		if (elProto.hasOwnProperty(i)) el[i] = elProto[i];
	}
	el.__manoLegacyExtended = true;
	return el;
};
