// Default POST result handler for client interface
// Used in legacy renderer, but may be used also in SPA

'use strict';

module.exports = function (form, postMessage) {
	var window = form.ownerDocument.defaultView;
	if (postMessage.message) window.showPostSuccessMessage.call(form, postMessage.message);
	else if (postMessage.error) window.showPostErrorMessage.call(form, postMessage.error);
	else console.log("Could not process post message!. Unrecognized instruction");
};
