// Resolve wether we want to apply translastion locale or not
// We can force to enable/disable locale via i18n=0 or i18n=1 query in url
// Or via i18n: true|false in env.json. otherwise locale is always loaded

'use strict';

var re = /(?:\?|&)i18n=(0|1)(?:&|$)/;

module.exports = function (env, locale) {
	var urlInstruction = location.search.match(re);
	if (urlInstruction) return (urlInstruction[1] === '0') ? null : locale;
	return (env.i18n === false) ? null : locale;
};
