// Initialize router
//
// Router holds urls/views mapping. It's populated with routes by auth.js
// that decides which website we should display (public or authenticated?)

'use strict';

var Router   = require('router')
  , location = require('./location');

module.exports = function (options) {
	var router = new Router(location);
	if (!options.useNewPostController) require('router/lib/client-post-controller')(router, document);
	return router;
};
