// Initialize view engine (this one changes the document content on url changes)
//
// Url changes are emitted by locationEmitter. Router listens to them and
// invokes the changes on view engine (its router that knows which views
// should be loaded on which url, and view engine does the change of the view
// from A to B)

'use strict';

module.exports = function (tplRequire) {
	return require('view/lib/client')(require('./domjs'), document,
		require('./location'), null, tplRequire);
};
