'use strict';

var template = require('es6-template-strings')
  , mano     = require('../../');

module.exports = function (src/*, env*/) {
	var env = arguments[1] || mano.env
	  , host, devHost;

	host = (env.static && env.static.host) || '/';
	devHost = env.dev ? '/' : host;
	return template(src, { STATIC_ROOT: host, STATIC_ROOT_APP: devHost });
};
