'use strict';

var isDate   = require('es5-ext/date/is-date')
  , isRegExp = require('es5-ext/reg-exp/is-reg-exp')

  , stringify = JSON.stringify;

module.exports = function (value) {
	var type;
	if (value === undefined) return 'undefined';
	if (value === null) return 'null';
	type = typeof value;
	if (type === 'boolean') return value ? 'true' : 'false';
	if (type === 'number') return String(value);
	if (type === 'string') return stringify(value);
	if (isDate(value)) return 'new Date(' + Number(value) + ')';
	if (isRegExp(value)) return String(value);
	if (value._kind_ && value.hasOwnProperty('__id__')) {
		return 'get(' + stringify(value.__id__) + ')';
	}
	if (type === 'function') return String(value);
	return null;
};
