// Copies apps client program generation script into system

'use strict';

var path = require('path')
  , copy = require('fs2/copy')

  , resolve = path.resolve

  , programTplPath = resolve(__dirname, 'program.tpl')
  , writeOpts = { intermediate: true }

  , scriptName = 'generate-apps-client-program';

module.exports = function (root) {
	return copy(programTplPath, resolve(root, 'bin', scriptName), writeOpts);
};
