// Generates client html index files for provided apps

'use strict';

var runPrograms = require('mano/server/utils/run-programs')
  , resolve     = require('path').resolve;

module.exports = function (root, appsList) {
	return runPrograms(appsList.map(function (appPath) {
		return ['node', resolve(root, appPath, 'bin/generate-html-index')];
	}), 'setup', 'generate-apps-html-index', { cwd: root });
};
